    public static String findGene(String dna, int where ){
        int startIndex = dna.indexOf("ATG", where);
       
        if(startIndex == -1 ){
            return "";
        }
        
        int taaFirstOcurrence = findStopCodon(dna, startIndex, "TAA");
        int tagFirstOcurrence = findStopCodon(dna, startIndex, "TAG");
        int tgaFirstOcurrence = findStopCodon(dna, startIndex, "TGA");
        
        int minIndex = 0;
        if (taaFirstOcurrence == -1 || (tgaFirstOcurrence != -1 && tgaFirstOcurrence <   taaFirstOcurrence)){
            minIndex = tgaFirstOcurrence;
        }else{
            minIndex = taaFirstOcurrence;
        }
        
        if (minIndex == -1 || (tagFirstOcurrence != -1 && tagFirstOcurrence< minIndex)){
            minIndex = tagFirstOcurrence;
        }
        if(minIndex == -1){
            return "";
        }
        return dna.substring(startIndex , minIndex + 3);
    }
        
