     public static double cgRatio(String dna){
         String C = "C";
         String G = "G";
         double cgRa = 0;
         int CTimes = howMany(C, dna);
         int GTimes = howMany(G, dna);
         int cgCount = CTimes + GTimes;
         cgRa = ( (float) cgCount ) / dna.length();
         
         
         System.out.println("Radio of C's and G's is : " + cgRa);
         return cgRa;
     }
     
     public static void cgRatioTest(){
         System.out.println(cgRatio("AtgMyGeneFiveATGGGGTAATGATAGCendFiveTGA"));
         
     }
     
     // Return how many string a is in string b by character
     public static int howMany(String stringa, String stringb){
        
        String Str = new String(stringb);
        stringb = Str.toUpperCase();
        int startIndex = stringb.indexOf(stringa);
        int concurrences = 0;
        if(startIndex == -1){
            return 0;
        }
        else{
            while(startIndex != -1){
               concurrences += 1;
               startIndex = stringb.indexOf(stringa, startIndex + 1);
            }

           
        }
        
        return concurrences;
     }
     
     public static int countCTG(String dna){
        String codon = "CTG";
        
         // 
        int startIndex = dna.indexOf(codon );
        int concurrences = 0;
        if(startIndex == -1){
            return 0;
        }
        else{
            while(startIndex != -1){
               concurrences += 1;
               startIndex = dna.indexOf(codon , startIndex + 3);
            }

           
        }
        
        return concurrences;
     }
     
     
